// Publicaci√≥n instant√°nea con validaci√≥n
const baseUrl = 'http://localhost:4123';

try {
  // Verificar sistema primero
  const healthCheck = await fetch(`${baseUrl}/health`);
  const health = await healthCheck.json();

  if (!health.status || health.status !== 'ok') {
    throw new Error('System not ready');
  }

  // Publicar en Instagram
  const response = await fetch(`${baseUrl}/api/agents/instagram/start`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ 
      source: 'manual',
      immediate: true 
    })
  });

  const result = await response.json();

  return {
    json: {
      success: true,
      message: 'üöÄ Content published successfully!',
      result: result,
      timestamp: new Date().toISOString()
    }
  };

} catch (error) {
  return {
    json: {
      success: false,
      message: '‚ùå Publication failed',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  };
}
